import{CONNECTED_APP_DOMAINS}from"../util.js";class DomainStorage{constructor(){const t=this._getDomainsFromStorage();this.domains=["http://localhost:*/*"].concat(t)}_getDomainsFromStorage(){const t=window.localStorage.getItem(CONNECTED_APP_DOMAINS);return t?"string"==typeof t?JSON.parse(t):t:[]}addDomainsToStorage(t){let o=this._getDomainsFromStorage();o=o.concat(t),this._setNewDomains(o)}removeDomainsFromStorage(t){let o=this._getDomainsFromStorage();o=o.filter(o=>!t.includes(o)),this._setNewDomains(o)}_setNewDomains(t){this.domains=t,window.localStorage.setItem(CONNECTED_APP_DOMAINS,JSON.stringify(t))}}export default DomainStorage;